import com.eviware.soapui.support.XmlHolder;

import groovy.time.TimeCategory;

String FirstName = org.apache.commons.lang.RandomStringUtils.random(9, true, true);
String LastName = org.apache.commons.lang.RandomStringUtils.random(6, true, true);
String emailID = org.apache.commons.lang.RandomStringUtils.random(6, true, true);
  emailID = emailID.concat("@gmail.com");

 //context.testCase.setPropertyValue("emailID",emailID);



Date randomDate(Range<Date> range) {
    range.from + new Random().nextInt(range.to - range.from + 1)
}


def start = Date.parse('yyyy-MM-dd', '2005-01-01')
def end   = Date.parse('yyyy-MM-dd', '2015-01-10')
Date dob1= randomDate(start..end)
def dob=dob1.format('yyyy-MM-dd');

def tc = testRunner.testCase.testSuite.project.testSuites["Funtional Testing of Customer Repository"]
.testCases['ScriptBasedValidation']

tc.testSteps['Properties'].setPropertyValue("FirstName",FirstName)
tc.testSteps['Properties'].setPropertyValue("LastName",LastName)
tc.testSteps['Properties'].setPropertyValue("DOB",dob)
tc.testSteps['Properties'].setPropertyValue("Email",emailID)

def email = context.expand('${Create Customer#Request#//*:email}')

log.info "The username is $email "


